import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import * as auth from '../utils/Auth';

function Register() {
  const [formValue, setFormValue] = useState({
      username: '',
      password: ''
    }
  )
  const [errorMessage, setErrorMessage] = useState({})

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormValue({
      ...formValue,
      [name]: value
    });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formValue.username || !formValue.password) {
      setErrorMessage('Both fields are required');
      return;
    } else {
      const {email, password} = formValue;
      auth.register(email, password).then((res) => {

      })
    }
  }

  return (
    <div className="register">
      <p className="register__welcome">
        Регистрация
      </p>
      <form className="register__form" onSubmit={handleSubmit}>
        <input type="text" value={formValue.username} onChange={handleChange} id="username" placeholder="Email"
               name="username"
               required
               minLength="2" maxLength="40"/>
        <span className="register__error"></span>
        <input type="text" value={formValue.password} onChange={handleChange} id="password" placeholder="Пароль"
               name="password" required
               minLength="2" maxLength="200"/>
        <span className="register__error"></span>
        <button type="submit" className="register__link">Зарегистрироваться</button>
      </form>
      <div className="register__signin">
        <p>Уже зарегистрированы?</p>
        <Link to="/signin" className="register__login-link" replace>Войти</Link>
      </div>
    </div>
  )
}

export default Register;
