import React from 'react';
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {Route, Routes, Navigate} from 'react-router-dom';
import Header from "../components/Header";
import Main from "../components/Main";
import Footer from "../components/Footer";
import ImagePopup from "./ImagePopup";
import {api} from "../utils/Api";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import ConfirmDeletePopup from "./ConfirmDeletePopup";
import Login from "./Login";
import Register from "./Register";
import PageNotFound from "./PageNotFound";
import * as auth from '../utils/Auth';
import {ProtectedRoute} from "./ProtectedRoute";

function App() {
  const [currentUser, setCurrentUser] = React.useState({
    avatar: '../images/loader.gif',
    name: 'Загрузка',
    about: 'Загрузка',
    _id: ''
  });
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [email, setEmail] = React.useState({email: ''});
  const [cards, setCards] = React.useState([]);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isConfirmDeletePopupOpen, setConfirmDeletePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [cardId, setCardId] = React.useState('');

  React.useEffect(() => {
    api.getUserInfo()
      .then((data) => {
        setCurrentUser(data)
      })
      .catch((error) => {
        console.log(error)
      });
  }, [])

  React.useEffect(() => {
    api.getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch
      ((error) => {
        console.log(error)
      });
  }, []);

  const handleLogin = () => {
    setLoggedIn(true);
  }

  const handleSignOut = () => {
    setLoggedIn(false);
  }

  const checkToken = () => {
    const jwt = localStorage.getItem('jwt');

    if (jwt) {
      auth.getContent()
        .then(user => {

        })
        .catch(error => console.log(error))
    }
  }

  function handleUpdateUser(newUserData) {
    api.setUserInfo(newUserData)
      .then((userData) => {
        setCurrentUser(userData);
        closeAllPopups()
      })
      .catch((error) => {
        console.log(error)
      });
  }

  function handleAddPlaceSubmit(data) {
    api.addNewCard(data)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(error)
      });
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((error) => {
        console.log(error)
      })
  }

  function handleUpdateAvatar(userData) {
    api.updateUserAvatar(userData)
      .then((userAvatar) => {
        setCurrentUser(userAvatar);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(error)
      });
  }

  React.useEffect(() => {
    function handleEscPress(evt) {
      if (evt.key === 'Escape') {
        closeAllPopups();
      }
    }

    const handleOverlayClick = (evt) => {
      if (evt.target.classList.contains('popup_opened')) {
        closeAllPopups();
      }
    };
    if (isEditProfilePopupOpen || isAddPlacePopupOpen || isEditAvatarPopupOpen || isConfirmDeletePopupOpen || selectedCard) {
      document.addEventListener('keydown', handleEscPress);
      document.addEventListener('click', handleOverlayClick);
      return () => {
        document.removeEventListener('keydown', handleEscPress);
        document.removeEventListener('mousedown', handleOverlayClick);
      }
    }
  }, [isEditProfilePopupOpen, isAddPlacePopupOpen, isEditAvatarPopupOpen, isConfirmDeletePopupOpen, selectedCard])

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard)
  }

  function handleCardDelete() {
    api.deleteUserCard(cardId)
      .then((res) => {
        const newCard = cards.filter((item) => item._id !== cardId);
        setCards(newCard);
        closeAllPopups();
      })
      .catch((error) => {
        console.log(error)
      });
  }

  function handleDeletePlaceClick(card) {
    setCardId(card);
    setConfirmDeletePopupOpen(true);
  }

  function closeAllPopups() {
    setEditAvatarPopupOpen(false);
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setConfirmDeletePopupOpen(false);
    setSelectedCard({});
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="Root">
        <div className="page">
          <Header loggedIn={loggedIn} handleSignOut={handleSignOut} email={email}/>
          <Routes>
            <Route path="/" element={loggedIn ? <Navigate to="/main"/> : <Navigate to="/signin" replace/>}/>
            <Route path="/main" element={
              <ProtectedRoute loggedIn={loggedIn} element={Main}
                              onEditAvatar={handleEditAvatarClick}
                              onEditProfile={handleEditProfileClick}
                              onAddPlace={handleAddPlaceClick}
                              cards={cards}
                              onCardClick={handleCardClick}
                              handleCardLike={handleCardLike}
                              handleCardDeleteClick={handleDeletePlaceClick}
              />
            }
            />
            <Route path="/signin" element={<Login handleLogin={handleLogin}/>}/>
            <Route path="/signup" element={<Register/>}/>
            <Route path='*' element={<PageNotFound/>}/>
          </Routes>
          <Footer loggedIn={loggedIn}/>

          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser}/>
          <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleAddPlaceSubmit}/>
          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar}/>
          <ConfirmDeletePopup isOpen={isConfirmDeletePopupOpen} onClose={closeAllPopups}
                              onConfirmDelete={handleCardDelete}/>
          <ImagePopup
            card={selectedCard}
            onClose={closeAllPopups}
          />

        </div>

      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
