import React, {useState} from 'react';

function Login() {
  const [formValue, setFormValue] = useState({
      username: '',
      password: ''
    }
  )
const [errorMessage, setErrorMessage] = useState({})

const handleChange = (e) => {
  const {name, value} = e.target;
  setFormValue({
    ...formValue,
    [name]: value
  });
}

const handleSubmit = (e) => {
  e.preventDefault();

  if (!formValue.username || !formValue.password) {
    setErrorMessage('Both fields are required');
    return;
  }
}

  return (
    <div className="login">
      <p className="login__welcome">
        Вход
      </p>
      <form className="login__form" onSubmit={handleSubmit}>
        <label htmlFor="username">
          <input type="text" value={formValue.username} onChange={handleChange} id="username" placeholder="Email"
                 name="username"
                 required
                 minLength="2" maxLength="40"/>
          <span className="name-input-error popup__input-error popup__input-error_span"></span>
        </label>
        <label htmlFor="password">
          <input type="text" value={formValue.password} onChange={handleChange} id="password" placeholder="Пароль"
                 name="password" required
                 minLength="2" maxLength="200"/>
          <span className="job-input-error popup__input-error popup__input-error_span"></span>
        </label>
        <button type="submit" className="login__link">Войти</button>
      </form>
      <div className="login__signup">
        <p>Ещё не зарегистрированы?</p>
        <Link to="/register" className="signup__link">Зарегистрироваться</Link>
      </div>
    </div>
  )
}

  export default Login;
